import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import toast from "react-hot-toast";
import axios from "axios";
import './Login.css'

const Login = () => {
  const navigate = useNavigate();
  //media
  const isNotMobile = useMediaQuery("(min-width: 1000px)");
  // states
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");

  //register ctrl
  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post("http://localhost:5000/api/v1/auth/login", { email, password });
      toast.success("Login Successfully");
      localStorage.setItem("authToken", true);
      navigate("/");
    } catch (err) {
      console.log(error);
      if (err.response.data.error) {
        setError(err.response.data.error);
      } else if (err.message) {
        setError(err.message);
      }
      setTimeout(() => {
        setError("");
      }, 5000);
    }
  };
  return (
    <div
    className={`signin-container ${error ? 'error' : ''}`}
    style={{
      width: isNotMobile? '30%': '40%',
      margin: '2rem auto',
      padding: '2rem',
      borderRadius: '5px',
      boxShadow: '5px 5px 5px rgba(0, 0, 0, 0.1)',
      backgroundColor: '#f9f9f9', // Change to your desired background color
    }}
  >
    {error && <div className="error-message">{error}</div>}
    <form onSubmit={handleSubmit}>
      <h3>Sign In</h3>
      <input
        type="email"
        placeholder="Email"
        required
        value={email}
        onChange={(e) => setEmail(e.target.value)}
      />
      <input
        type="password"
        placeholder="Password"
        required
        value={password}
        onChange={(e) => setPassword(e.target.value)}
      />
      <button type="submit">Sign In</button>
      <p>
        Don't have an account? <a href="/register">Please Register</a>
      </p>
    </form>
  </div>


