      className={`scifi-image-container ${error ? 'error' : ''}`}
      style={{
        width: '40%',
        margin: '2rem auto',
        padding: '2rem',
        borderRadius: '5px',
        boxShadow: '5px 5px 5px rgba(0, 0, 0, 0.1)',
        backgroundColor: '#f99f8', // Change to your desired background color
